import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function BadgeDisplay() {
  const { id } = useParams(); // Get the badge ID from the URL
  const [badge, setBadge] = useState(null); // State to store the fetched badge data

  useEffect(() => {
    const fetchBadge = async () => {
      try {
        // Fetch the badge data from the backend using the badge ID
        const response = await axios.get(`http://localhost:5000/api/badges/${id}`);
        console.log(response.data)
        setBadge(response.data); // Set the fetched data into state
      } catch (error) {
        console.error('Error fetching badge', error);
      }
    };

    fetchBadge();
  }, [id]);

  console.log(badge)
  // Show a loading message while the badge data is being fetched
  if (!badge) return <div>Loading...</div>;

  // Display the badge data and the QR code image
  return (
    <div className="badge-display">
      <h1>{badge.name}</h1>
      <p>University: {badge.university}</p>
      <p>Major: {badge.major}</p>
      <p>Graduation Date: {badge.graduation_date}</p>
      <p>
        GitHub: <a href={badge.github} target="_blank" rel="noopener noreferrer">{badge.github}</a>
      </p>
      {/* Display the QR code image */}
      <img src={`http://localhost:5000/${badge.qr_code_url}`} alt="QR Code" />
    </div>
  );
}

export default BadgeDisplay;
